import React from 'react';
import './App.css';
import Todo from './components/todo/todo';

const App = () => {
	let todos = [
		{ id: 1, completed: false, title: 'Buy beer' },
		{ id: 2, completed: false, title: 'Buy fish' },
		{ id: 3, completed: false, title: 'Buy bread' }
	]
	const onChange = (id) => {
		todos = todos.map(todo => {
			if (todo.id === id) {
				todo.completed = !todo.completed
			}
			return todo
		})
	}

	return (
		<div className={'container'} >
			<h1>React todo list</h1>
			<Todo todos={todos} onChange={onChange} />
		</div>
	)
}

export default App;
